import React, { useState, KeyboardEvent } from 'react';
import './App.css';

interface Message {
    text: string;
    type: 'user' | 'reply';
}

function App() {
    const [messages, setMessages] = useState<Message[]>([]);
    const [input, setInput] = useState('');
    const [file, setFile] = useState<File | null>(null);
    const [canvas, setCanvas] = useState<string[]>([]);

    const handleSend = () => {
        if (input.trim()) {
            // Add user message
            setMessages([...messages, { text: input, type: 'user' }]);

            // Simulate a response (in a real app, this would come from the backend)
            const response = `Response to: ${input}`;
            setMessages(prev => [...prev, { text: response, type: 'reply' }]);

            // Only update canvas if there's generated content
            const generatedContent = Math.random() > 0.5 ? `Generated content for: ${input}` : null;
            if (generatedContent) {
                setCanvas([...canvas, generatedContent]);
            }

            setInput('');
        }
    };

    const handleKeyPress = (event: KeyboardEvent<HTMLInputElement>) => {
        if (event.key === 'Enter') {
            handleSend();
        }
    };

    const handleFileChange = (event: React.ChangeEvent<HTMLInputElement>) => {
        if (event.target.files && event.target.files[0]) {
            setFile(event.target.files[0]);
        }
    };

    const clearChat = () => {
        setMessages([]);
        setCanvas([]);
        setFile(null);
    };

    return (
        <div className="App">
            <header className="App-header">
                <h1>AgentHub Chat</h1>
            </header>
            <div className="chat-container">
                <div className="messages">
                    {messages.map((msg, index) => (
                        <div key={index} className={`message ${msg.type}`}>
                            <span className="message-label">{msg.type === 'user' ? 'User>' : 'Reply>'}</span>
                            {msg.text}
                        </div>
                    ))}
                </div>
                <div className="input-container">
                    <input
                        type="text"
                        value={input}
                        onChange={(e) => setInput(e.target.value)}
                        onKeyPress={handleKeyPress}
                        placeholder="Type a message... (Press Enter to send)"
                    />
                    <input type="file" onChange={handleFileChange} />
                    <button onClick={handleSend}>Send</button>
                    <button onClick={clearChat} className="clear-button">Clear Chat</button>
                </div>
            </div>
            <div className="canvas-container">
                <h2>Canvas</h2>
                {canvas.map((content, index) => (
                    <div key={index} className="canvas-content">{content}</div>
                ))}
            </div>
        </div>
    );
}

export default App;
