import React, { useState, KeyboardEvent } from 'react';
import './App.css';

function App() {
    const [messages, setMessages] = useState<string[]>([]);
    const [input, setInput] = useState('');
    const [file, setFile] = useState<File | null>(null);
    const [canvas, setCanvas] = useState<string[]>([]);

    const handleSend = () => {
        if (input.trim()) {
            setMessages([...messages, input]);
            setInput('');
            // Add only the generated result to canvas, not the message
            setCanvas([...canvas, `Generated content for: ${input}`]);
        }
    };

    const handleKeyPress = (event: KeyboardEvent<HTMLInputElement>) => {
        if (event.key === 'Enter') {
            handleSend();
        }
    };

    const handleFileChange = (event: React.ChangeEvent<HTMLInputElement>) => {
        if (event.target.files && event.target.files[0]) {
            setFile(event.target.files[0]);
        }
    };

    const clearChat = () => {
        setMessages([]);
        setCanvas([]);
        setFile(null);
    };

    return (
        <div className="App">
            <header className="App-header">
                <h1>AgentHub Chat</h1>
            </header>
            <div className="chat-container">
                <div className="messages">
                    {messages.map((msg, index) => (
                        <div key={index} className="message">{msg}</div>
                    ))}
                </div>
                <div className="input-container">
                    <input
                        type="text"
                        value={input}
                        onChange={(e) => setInput(e.target.value)}
                        onKeyPress={handleKeyPress}
                        placeholder="Type a message... (Press Enter to send)"
                    />
                    <input type="file" onChange={handleFileChange} />
                    <button onClick={handleSend}>Send</button>
                    <button onClick={clearChat} className="clear-button">Clear Chat</button>
                </div>
            </div>
            <div className="canvas-container">
                <h2>Canvas</h2>
                {canvas.map((content, index) => (
                    <div key={index} className="canvas-content">{content}</div>
                ))}
            </div>
        </div>
    );
}

export default App;
